import { promptMovement } from "./Movement.js";
import { map, mapObj, restartGame } from "./main.js"
import inquirer from "inquirer";

class Entity {
    isAlive = true;

    constructor(name, hp, defense, attack, sprite) {
        this.name = name;
        this.hp = hp;
        this.defense = defense;
        this.attack = attack;
        this.sprite = sprite;
    }
}

class Player extends Entity {
    xCoord = 0;
    yCoord = 0;

    constructor(name, hp, defense, attack, sprite) {
        super(name, hp, defense, attack, sprite);
    }

    move (direction) {
        if (this.xCoord === 0 && direction === "left") {
            return false;
        }
        if (this.yCoord === 0 && direction === "down") {

            return false;
        }
        if (this.xCoord === map.width - 1 && direction === "right") {
            return false;
        }
        if (this.yCoord === map.height - 1 && direction === "up") {
            return false;
        }

        if (direction === "up") {
            this.yCoord += 1;
        } else if (direction === "down") {
            this.yCoord -=1;
        } else if (direction === "left") {
            this.xCoord -=1;
        } else {
            this.xCoord += 1;
        }

        return true;
    }
}

class Potion {
    constructor(name, sprite, effect) {
        this.name = name;
        this.sprite = sprite;
        this.effect = effect;
    }
}

const getRandomEvent = () => {
    if (map.lObj[`row${player.yCoord}`][player.xCoord].alreadyExplored) {
        console.log("Tile already explored...\n");
        promptMovement();
        return;
    }
    if (player.xCoord === map.width - 1 && player.yCoord === map.height - 1) {
        return "win";
    }
    let percentageChance = Math.random();
    if (percentageChance <= 0.33) {
        battleIntro();
    } else if (percentageChance <= 0.66) {
        getItem();
    } else {
        console.log("Nothing happened...\n");
        promptMovement();
    }
}

