const enemyAttack = async (enemy, player) => {  
    const enemyDamage = Math.max(0, enemy.attack - player.defense);

    await new Promise((resolve) => {
        setTimeout(() => {
            console.log(`\n${enemy.name} attacks`);
            player.hp -= enemyDamage;
            console.log(`${enemy.name} dealt ${enemyDamage} damage`);

            if (player.hp <= 0) {
                player.isAlive = false;
                console.log(`You've been defeated\n`);
                restartGame();
                return;
            } else {
                console.log(`Your remaining hp: ${player.hp}`);
            }

            resolve();
            }, 1000);
        })
    }

const battleSequence = async (enemy) => {
    let firstTurn = true;

    while (enemy.isAlive && player.isAlive) {
        console.clear();
        map.displayMap(mapObj);

        if (firstTurn) {
            console.log(`You bumped into ${enemy.name} ${enemy.sprite}`);
            firstTurn = false;
        } else {
            console.log(enemy.name, enemy.sprite);
        }

        console.log(`<ATK: ${enemy.attack}> <DEF: ${enemy.defense}> <HP: ${enemy.hp}>\n`);

        const answer = await inquirer.prompt([
            {
                type: 'list',
                name: 'action',
                message: 'What would you like to do?',
                choices: [
                    'Attack',
                    'Run',
                ],
                         
            },
        ]);

        if (answer.action === 'Attack') {
            const damage = Math.max(0, player.attack - enemy.defense);

            await new Promise((resolve) => {
                setTimeout(() => {
                    console.log(`\nYou Attack`);
                    enemy.hp -= damage;
                    console.log(`You dealt ${damage} damage to ${enemy.name}`);

                    if (enemy.hp <= 0) {
                        enemy.isAlive = false;
                        enemy.hp = 0;
                        console.log(`${enemy.name} ${enemy.sprite} has been defeated\n`);
                    } else {
                        console.log(`${enemy.name} remaining hp: ${enemy.hp}`);
                    }

                    resolve();
                }, 1000);
            });

            if (enemy.hp > 0) {
                enemyAttack(enemy, player)
                if (player.hp <= 0) {
                    return;
                }
            } 
        
        } else {
            const percentageChance = Math.random();
            if (percentageChance <= 0.5) {
                console.log("\nSuccesfully ran away\n")
                promptMovement();
                return;
            } else {
                console.log("\nCouldn't run away\n")
                enemyAttack(enemy, player);
            }
        }

        
        await new Promise((resolve) => setTimeout(resolve, 2000));
        

        if (!player.isAlive) {
            
        } else if (!enemy.isAlive) {
                await promptMovement();
            }
    }
};
            
